mapper.select:
SELECT NAME
FROM CLUB
WHERE NAME = #{clubName}

SELECT FILE_NAME
FROM CLUB_IMAGE_FILE
WHERE NO = #{no}

mapper.update:
UPDATE CLUB
SET
CLUB_DESCRIPTION = #{clubDescription}
,AGE_LIMIT = #{ageLimit}
,SIGNUP_LIMIT = #{signupLimit}
WHERE NO = #{clubNo}

UPDATE CLUB_IMAGE_FILE
SET FILE_URL = #{fileUrl}
    ,FILE_NAME = #{fileName}
WHERE NO = #{no}

choose.when:
DELETE FROM MEMBER_CLUB
WHERE MEMBER_NO = #{memberNo}
    AND CLUB_NO = #{clubNo}

mapper.update:
UPDATE CLUB
SET DEL_YN = 'Y'
WHERE NO = #{clubNo}
