mapper.select:
SELECT NAME
FROM CLUB
WHERE NAME = #{clubName}

SELECT FILE_NAME
FROM CLUB_IMAGE_FILE
WHERE NO = #{no}

mapper.update:
UPDATE CLUB
SET
CLUB_DESCRIPTION = #{clubDescription}
,AGE_LIMIT = #{ageLimit}
,SIGNUP_LIMIT = #{signupLimit}
WHERE NO = #{clubNo}

UPDATE CLUB_IMAGE_FILE
SET FILE_URL = #{fileUrl}
    ,FILE_NAME = #{fileName}
WHERE NO = #{no}

choose.when:
UPDATE MEMBER_CLUB

    SET ADMIN_YN = 'N'
    WHERE MEMBER_NO = #{memberNo}
        AND CLUB_NO = #{no}


    SET ADMIN_YN = 'Y'
    WHERE MEMBER_NO = #{memberNo}
        AND CLUB_NO = #{no}

DELETE FROM MEMBER_CLUB
WHERE MEMBER_NO = #{memberNo}
    AND CLUB_NO = #{no}

mapper.delete:
DELETE FROM MEMBER_CLUB
WHERE CLUB_NO = #{no}
    AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE CLUB
SET DEL_YN = 'Y'
WHERE NO = #{clubNo}

mapper.select:
SELECT DEL_YN
FROM CLUB
WHERE NO = #{clubNo}
