mapper.insert:
        INSERT INTO MEMBER
		(
		    NO
            ,NAME
            ,ID
            ,PWD
            ,NICK
            ,PHONE
            ,EMAIL
            ,PROFILE_IMG
            ,RESI_NUM
		)
		VALUES
		(
		    SEQ_MEMBER_NO.NEXTVAL
            , #{name}
		    , #{id}
		    , #{pwd}
		    , #{nick}
            , #{phone}
            , #{email}
            , #{profileImg}
            , #{resiNum}
		)

mapper.update:
		UPDATE MEMBER
			SET
				NO
	           ,ID = #{id}
	           ,PWD = #{pwd}
	           ,NICK = #{nick}
	           ,PHONE = #{phone}
	           ,EMAIL = #{email}
	           ,PROFILE_IMG = #{profileImg}
           WHERE NO = #{no}
		   AND QUIT_YN = 'N'
